							<script type="text/javascript">
							jQuery(document).ready(function(){
							setTimeout(function() {
							     jQuery( ".fieldset-candidate_experience a.resume-manager-add-row" ).trigger( "click" );
							 },10);  
							 jQuery(".uploadbutton").on("click",function(){
							       jQuery( ".slim-file-hopper" ).trigger( "click" );
							 });
							 var supported = Slim.supported; 
							 if(supported){
							     jQuery('.v_originalfield').remove();

							jQuery('#myCropper').slim({
							       crop: {
							           x: 0,
							           y: 0,
							           width: 100,
							           height: 100
							       },
							       size:{
							         width: 320,
							         height: 320,  
							       },
							       push: "true",
							       service: ajaxurl,
							       download: false,
							       label: 'Choose your image here.',
							       buttonConfirmLabel: 'Ok',
							       ratio:'1:1',
							       didUpload:function(err, data, res) {
							           if(res.status === "success" ){
							             jQuery('.slim').show();  
							             jQuery('#imagename').attr("data-imagename",res.name);
							             jQuery('input[name="current_candidate_photo"]').val(res.url);
							             jQuery('input[name="candidate_photo"]').val(res.url);
							             jQuery('.uploadbutton').hide();
							           }
							        },
							        didRemove:function(data){
							           var name = jQuery('#imagename').attr('data-imagename'); 
							           jQuery('input[name="current_candidate_photo"]').val("");
							           jQuery('input[name="candidate_photo"]').val("");
							           jQuery('#imagename').attr("data-imagename",'');
							           var url = ajaxurl;
							           var xhr = new XMLHttpRequest();
							           xhr.open('GET.html', url+ '?action=custom_file_remove&name=' + name, true);
							           xhr.send();
							           jQuery('.uploadbutton').show();
							           jQuery('.slim').hide();
							        },
							        willSave:function(data, ready){
							           var name = jQuery('#imagename').attr('data-imagename'); 
							           if(name != "" && name != null && name != 'undefined'){
							                 jQuery('#imagename').attr("data-imagename",'');
							                 var url = ajaxurl;
							                 var xhr = new XMLHttpRequest();
							                 xhr.open('GET.html', url+ '?action=custom_file_remove&name=' + name, true);
							                 xhr.send();
							             }
							         ready(data);
							        }
							 });
							 }else{
							    jQuery('.v_customfield').remove();
							}
							 jQuery('.slim').hide();
							});
							</script> 
